import React, { Component } from "react";


class Directions extends Component {
  constructor() {
    super();
    this.state = {
      message: "START",
    };
  }

  changemessage1() {
    this.setState({
      message: "MOVING FRONT",
    });
  }
  changemessage2() {
    this.setState({
      message: "MOVING RIGHT",
    });
  }
  changemessage3() {
    this.setState({
      message: "MOVING BACK",
    });
  }
  changemessage4() {
    this.setState({
      message: "MOVING LEFT",
    });
  }

  render() {
    return (
      <div>
        <h1>{this.state.message}</h1>
        <button onClick={() => this.changemessage1()}>Front</button>
        <button onClick={() => this.changemessage2()}>Right</button>
        <button onClick={() => this.changemessage3()}>Back</button>
        <button onClick={() => this.changemessage4()}>Left</button>
      </div>
    );
  }
}

export default Directions;
